mapper.select:
SELECT id,level,name,descr,createDate,updateDate from xsl_hunter_level
name like concat('%',#{queryText},'%') and
id >=1 order by id limit #{start},#{size}

SELECT id,level,name,descr,createDate,updateDate from xsl_master_level
name like concat('%',#{queryText},'%') and
id >=1 order by id limit #{start},#{size}

SELECT id,name,sex,phone,email,createDate,updateDate from xsl_admin
name like concat('%',#{queryText},'%') and
state = 1 and id >=1 order by id limit #{start},#{size}

SELECT id,hunterLevelId,level,experience,createDate,updateDate from xsl_hunter_level_experience
and name like concat('%',#{queryText},'%')
id >=1 order by id limit #{start},#{size}

SELECT id,masterLevelId,level,experience,createDate,updateDate from xsl_master_level_experience
and name like concat('%',#{queryText},'%')
id >=1 order by id limit #{start},#{size}

SELECT id,manager_name,creat_time,last_login_date from xsl_manager
and manager_name like concat('%',#{queryText},'%')
id >=1 order by id limit #{start},#{size}
